// ML-Based Recommendation Engine
class RecommendationEngine {
  constructor() {
    this.weights = {
      skillRelevance: 0.3,
      difficultyMatch: 0.25,
      careerAlignment: 0.2,
      peerSimilarity: 0.15,
      trendingFactor: 0.1
    };
  }

  generateRecommendations(userProfile, allTests, options = {}) {
    const { limit = 10, focus = 'balanced' } = options;
    
    const recommendations = allTests
      .filter(test => !userProfile.completedTests?.includes(test.id))
      .map(test => ({
        test,
        score: this.calculateRecommendationScore(test, userProfile, focus),
        reasoning: this.generateReasoning(test, userProfile)
      }))
      .sort((a, b) => b.score - a.score)
      .slice(0, limit);
    
    return recommendations;
  }

  calculateRecommendationScore(test, userProfile, focus) {
    let score = 0;
    
    // Skill relevance
    score += this.calculateSkillRelevance(test, userProfile) * this.weights.skillRelevance;
    
    // Difficulty match
    score += this.calculateDifficultyMatch(test, userProfile) * this.weights.difficultyMatch;
    
    // Career alignment
    score += this.calculateCareerAlignment(test, userProfile) * this.weights.careerAlignment;
    
    // Trending factor
    score += this.calculateTrendingFactor(test) * this.weights.trendingFactor;
    
    // Focus-specific adjustments
    score = this.applyFocusAdjustments(score, focus, test, userProfile);
    
    return Math.min(1.0, Math.max(0.0, score));
  }

  calculateSkillRelevance(test, userProfile) {
    const userSkills = Array.from(userProfile.skillLevels?.keys() || []);
    const matchingSkills = test.skills.filter(skill => userSkills.includes(skill));
    return matchingSkills.length / Math.max(1, test.skills.length);
  }

  calculateDifficultyMatch(test, userProfile) {
    const userLevel = this.estimateUserLevel(userProfile);
    const difficultyMap = { 'Beginner': 1, 'Intermediate': 2, 'Advanced': 3 };
    
    const userLevelNum = difficultyMap[userLevel] || 1;
    const testLevelNum = difficultyMap[test.difficulty] || 1;
    
    const difference = Math.abs(userLevelNum - testLevelNum);
    return Math.max(0, 1 - (difference * 0.3));
  }

  calculateCareerAlignment(test, userProfile) {
    const careerGoals = userProfile.careerGoals || [];
    if (careerGoals.length === 0) return 0.5;
    
    return careerGoals.some(goal => 
      test.skills.some(skill => skill.toLowerCase().includes(goal.toLowerCase())) ||
      test.jobRoles?.some(role => role.toLowerCase().includes(goal.toLowerCase()))
    ) ? 1.0 : 0.3;
  }

  calculateTrendingFactor(test) {
    // Simulate trending based on test creation date and popularity
    const now = new Date();
    const testDate = new Date(test.createdAt || now);
    const daysSinceCreation = (now - testDate) / (1000 * 60 * 60 * 24);
    
    const recencyFactor = Math.max(0, 1 - (daysSinceCreation / 365));
    const popularityFactor = Math.min(1, (test.popularity || 0) / 1000);
    
    return (recencyFactor + popularityFactor) / 2;
  }

  applyFocusAdjustments(baseScore, focus, test, userProfile) {
    switch (focus) {
      case 'skill-building':
        return baseScore * (1 + this.calculateSkillRelevance(test, userProfile));
      case 'career-focused':
        return baseScore * (1 + this.calculateCareerAlignment(test, userProfile));
      case 'challenge':
        const userLevel = this.estimateUserLevel(userProfile);
        return test.difficulty === 'Advanced' && userLevel !== 'Beginner' ? baseScore * 1.5 : baseScore;
      case 'trending':
        return baseScore * (1 + this.calculateTrendingFactor(test));
      default:
        return baseScore;
    }
  }

  generateReasoning(test, userProfile) {
    const reasons = [];
    
    if (this.calculateSkillRelevance(test, userProfile) > 0.7) {
      reasons.push('Matches your existing skills');
    }
    
    if (this.calculateCareerAlignment(test, userProfile) > 0.8) {
      reasons.push('Aligns with your career goals');
    }
    
    if (this.calculateTrendingFactor(test) > 0.6) {
      reasons.push('Currently trending in the industry');
    }
    
    if (this.calculateDifficultyMatch(test, userProfile) > 0.8) {
      reasons.push('Perfect difficulty level for you');
    }
    
    return reasons.length > 0 ? reasons.join(', ') : 'Recommended based on your profile';
  }

  estimateUserLevel(userProfile) {
    const completedTests = userProfile.completedTests?.length || 0;
    const averageScore = userProfile.averageScore || 0;
    
    if (completedTests < 5 || averageScore < 60) return 'Beginner';
    if (completedTests < 15 || averageScore < 80) return 'Intermediate';
    return 'Advanced';
  }
}

// Advanced Anti-Cheating System
class AntiCheatSystem {
  constructor() {
    this.behaviorAnalytics = new Map();
    this.suspiciousActivities = new Map();
    this.integrityScore = new Map();
    this.realTimeMonitoring = true;
  }

  // Initialize anti-cheat for a test session
  initializeSession(userId, testId, antiCheatConfig) {
    const sessionId = `${userId}_${testId}_${Date.now()}`;
    
    this.behaviorAnalytics.set(sessionId, {
      startTime: Date.now(),
      questionTimings: [],
      tabSwitches: 0,
      copyPasteAttempts: 0,
      keystrokePatterns: [],
      mouseMovements: [],
      windowFocusLoss: 0,
      screenshotAttempts: 0,
      suspiciousKeystrokes: 0,
      browserResizes: 0,
      rightClickAttempts: 0,
      devToolsOpened: false
    });

    this.integrityScore.set(sessionId, 100); // Start with perfect score
    
    return {
      sessionId,
      monitoringConfig: antiCheatConfig,
      integrityThreshold: 70, // Below this triggers review
      realTimeAlerts: true
    };
  }

  // Record suspicious activity
  recordActivity(sessionId, activityType, details = {}) {
    if (!this.behaviorAnalytics.has(sessionId)) return;
    
    const session = this.behaviorAnalytics.get(sessionId);
    const currentIntegrity = this.integrityScore.get(sessionId) || 100;
    
    let penaltyScore = 0;
    
    switch (activityType) {
      case 'TAB_SWITCH':
        session.tabSwitches++;
        penaltyScore = session.tabSwitches > 3 ? 15 : 5;
        break;
      case 'COPY_PASTE':
        session.copyPasteAttempts++;
        penaltyScore = 20;
        break;
      case 'WINDOW_FOCUS_LOSS':
        session.windowFocusLoss++;
        penaltyScore = session.windowFocusLoss > 2 ? 10 : 3;
        break;
      case 'SCREENSHOT_ATTEMPT':
        session.screenshotAttempts++;
        penaltyScore = 25;
        break;
      case 'RIGHT_CLICK':
        session.rightClickAttempts++;
        penaltyScore = session.rightClickAttempts > 5 ? 8 : 2;
        break;
      case 'DEV_TOOLS':
        session.devToolsOpened = true;
        penaltyScore = 30;
        break;
      case 'SUSPICIOUS_KEYSTROKE':
        session.suspiciousKeystrokes++;
        penaltyScore = 5;
        break;
      case 'BROWSER_RESIZE':
        session.browserResizes++;
        penaltyScore = session.browserResizes > 3 ? 5 : 1;
        break;
      case 'FAST_COMPLETION':
        // Answered too quickly for human reading speed
        penaltyScore = details.severity * 10;
        break;
      case 'PATTERN_ANOMALY':
        // Unusual answer patterns (all C's, alternating, etc.)
        penaltyScore = details.confidence * 15;
        break;
    }
    
    const newIntegrity = Math.max(0, currentIntegrity - penaltyScore);
    this.integrityScore.set(sessionId, newIntegrity);
    
    // Log suspicious activity
    if (!this.suspiciousActivities.has(sessionId)) {
      this.suspiciousActivities.set(sessionId, []);
    }
    
    this.suspiciousActivities.get(sessionId).push({
      type: activityType,
      timestamp: Date.now(),
      details,
      penaltyApplied: penaltyScore,
      integrityAfter: newIntegrity
    });
    
    return {
      currentIntegrity: newIntegrity,
      flagged: newIntegrity < 70,
      immediateAction: newIntegrity < 50
    };
  }

  // Analyze keystroke patterns for authenticity
  analyzeKeystrokePattern(sessionId, keystrokeData) {
    if (!this.behaviorAnalytics.has(sessionId)) return;
    
    const session = this.behaviorAnalytics.get(sessionId);
    session.keystrokePatterns.push({
      timestamp: Date.now(),
      timeBetweenKeys: keystrokeData.timeBetweenKeys,
      typingSpeed: keystrokeData.typingSpeed,
      rhythm: keystrokeData.rhythm
    });
    
    // Detect bot-like behavior
    const avgTimeBetween = keystrokeData.timeBetweenKeys.reduce((a, b) => a + b, 0) / keystrokeData.timeBetweenKeys.length;
    const variance = this.calculateVariance(keystrokeData.timeBetweenKeys);
    
    // Very consistent timing suggests bot
    if (variance < 10 && avgTimeBetween < 50) {
      return this.recordActivity(sessionId, 'SUSPICIOUS_KEYSTROKE', {
        reason: 'Bot-like consistent timing',
        variance,
        avgTimeBetween
      });
    }
    
    // Extremely fast typing suggests copy-paste
    if (keystrokeData.typingSpeed > 200) {
      return this.recordActivity(sessionId, 'SUSPICIOUS_KEYSTROKE', {
        reason: 'Unnaturally fast typing',
        typingSpeed: keystrokeData.typingSpeed
      });
    }
    
    return { legitimate: true };
  }

  // Analyze answer patterns for cheating indicators
  analyzeAnswerPattern(sessionId, answers) {
    const patterns = this.detectAnswerPatterns(answers);
    
    if (patterns.allSameOption && answers.length > 5) {
      return this.recordActivity(sessionId, 'PATTERN_ANOMALY', {
        type: 'all_same_option',
        confidence: 0.8
      });
    }
    
    if (patterns.alternatingPattern && answers.length > 8) {
      return this.recordActivity(sessionId, 'PATTERN_ANOMALY', {
        type: 'alternating_pattern',
        confidence: 0.7
      });
    }
    
    if (patterns.sequentialPattern && answers.length > 6) {
      return this.recordActivity(sessionId, 'PATTERN_ANOMALY', {
        type: 'sequential_pattern',
        confidence: 0.9
      });
    }
    
    return { legitimate: true };
  }

  // Check question completion time against expected human reading speed
  analyzeQuestionTime(sessionId, questionLength, timeSpent, difficulty) {
    const wordsInQuestion = questionLength / 5; // Approximate words
    const minimumReadTime = wordsInQuestion * 60; // 60ms per word minimum
    const expectedTime = {
      'Easy': minimumReadTime * 2,
      'Medium': minimumReadTime * 3,
      'Hard': minimumReadTime * 4
    }[difficulty] || minimumReadTime * 2.5;
    
    if (timeSpent < expectedTime * 0.5) {
      const severity = Math.min(1, expectedTime / timeSpent);
      return this.recordActivity(sessionId, 'FAST_COMPLETION', {
        expectedTime,
        actualTime: timeSpent,
        severity
      });
    }
    
    return { legitimate: true };
  }

  // Generate final integrity report
  generateIntegrityReport(sessionId) {
    const session = this.behaviorAnalytics.get(sessionId);
    const suspiciousActivities = this.suspiciousActivities.get(sessionId) || [];
    const finalIntegrity = this.integrityScore.get(sessionId) || 0;
    
    const report = {
      sessionId,
      finalIntegrityScore: finalIntegrity,
      testDuration: Date.now() - session.startTime,
      flags: {
        tabSwitches: session.tabSwitches,
        copyPasteAttempts: session.copyPasteAttempts,
        windowFocusLoss: session.windowFocusLoss,
        screenshotAttempts: session.screenshotAttempts,
        devToolsOpened: session.devToolsOpened,
        suspiciousKeystrokes: session.suspiciousKeystrokes
      },
      suspiciousActivities: suspiciousActivities.length,
      recommendation: this.getTestResultRecommendation(finalIntegrity),
      humanLikelihoodScore: this.calculateHumanLikelihood(session),
      trustLevel: this.calculateTrustLevel(finalIntegrity, session)
    };
    
    return report;
  }

  // Helper methods
  calculateVariance(numbers) {
    const mean = numbers.reduce((a, b) => a + b, 0) / numbers.length;
    const variance = numbers.reduce((sum, num) => sum + Math.pow(num - mean, 2), 0) / numbers.length;
    return variance;
  }

  detectAnswerPatterns(answers) {
    return {
      allSameOption: new Set(answers).size === 1,
      alternatingPattern: this.isAlternatingPattern(answers),
      sequentialPattern: this.isSequentialPattern(answers)
    };
  }

  isAlternatingPattern(answers) {
    if (answers.length < 4) return false;
    for (let i = 2; i < answers.length; i++) {
      if (answers[i] !== answers[i - 2]) return false;
    }
    return true;
  }

  isSequentialPattern(answers) {
    if (answers.length < 4) return false;
    for (let i = 1; i < answers.length; i++) {
      if (answers[i] !== (answers[i - 1] + 1) % 4) return false;
    }
    return true;
  }

  calculateHumanLikelihood(session) {
    let score = 100;
    
    // Deduct for bot-like behavior
    if (session.keystrokePatterns.length > 0) {
      const avgVariance = session.keystrokePatterns.reduce((sum, pattern) => {
        return sum + this.calculateVariance(pattern.timeBetweenKeys || [100]);
      }, 0) / session.keystrokePatterns.length;
      
      if (avgVariance < 20) score -= 30; // Very consistent = bot-like
    }
    
    // Natural human behavior includes some tab switches, mouse movements
    if (session.tabSwitches === 0 && session.windowFocusLoss === 0) {
      score -= 10; // Too perfect = suspicious
    }
    
    return Math.max(0, score);
  }

  calculateTrustLevel(integrity, session) {
    if (integrity >= 90 && session.tabSwitches <= 2) return 'HIGH';
    if (integrity >= 70 && session.copyPasteAttempts === 0) return 'MEDIUM';
    if (integrity >= 50) return 'LOW';
    return 'VERY_LOW';
  }

  getTestResultRecommendation(integrity) {
    if (integrity >= 90) return 'ACCEPT_RESULT';
    if (integrity >= 70) return 'ACCEPT_WITH_NOTE';
    if (integrity >= 50) return 'MANUAL_REVIEW_REQUIRED';
    return 'REJECT_RESULT';
  }
}

