  // Apply MCQ security to individual test
  applyMCQSecurityToTest(test) {
    // Check if test is MCQ type or has MCQ format questions
    if (test.type === 'MCQ' || this.hasMCQFormat(test)) {
      // Apply MCQ security if not already configured with maximum security
      if (!test.antiCheatMechanisms || !test.antiCheatMechanisms.securityLevel?.includes('MCQ_')) {
        const baseTimePerQuestion = test.antiCheatMechanisms?.timePerQuestion || 120;
        
        test.antiCheatMechanisms = {
          ...this.getMCQEnhancedSecurity(),
          ...test.antiCheatMechanisms, // Preserve existing settings
          timePerQuestion: baseTimePerQuestion,
          minimumTimePerQuestion: Math.max(15, Math.floor(baseTimePerQuestion * 0.1)),
          maximumTimePerQuestion: Math.min(300, Math.floor(baseTimePerQuestion * 1.5)),
          securityLevel: `MCQ_${test.category}_MAXIMUM_VERIFICATION`,
          cheatDetectionSensitivity: 'ULTRA_HIGH',
          jobProfileVerification: 'REQUIRED'
        };
      }
    }
  }

  // Check if test has MCQ format questions
  hasMCQFormat(test) {
    if (!test.questions || test.questions.length === 0) return false;
    
    // Check if first few questions have MCQ format (options array)
    const sampleQuestions = test.questions.slice(0, 3);
    return sampleQuestions.some(q => q.options && Array.isArray(q.options) && q.options.length > 1);
  }

