  getPopularSearchTerms(limit = 15) {
    const skillCounts = {};
    const companyCounts = {};
    const roleCounts = {};

    this.tests.forEach(test => {
      test.skills.forEach(skill => {
        skillCounts[skill] = (skillCounts[skill] || 0) + test.popularity;
      });
      
      test.companies.forEach(company => {
        companyCounts[company] = (companyCounts[company] || 0) + test.popularity;
      });
      
      test.jobRoles.forEach(role => {
        roleCounts[role] = (roleCounts[role] || 0) + test.popularity;
      });
    });

    const popularTerms = [
      ...Object.entries(skillCounts),
      ...Object.entries(companyCounts),
      ...Object.entries(roleCounts)
    ]
    .sort(([,a], [,b]) => b - a)
    .slice(0, limit)
    .map(([term]) => term);

    return popularTerms;
  }

  // Search by skill level and get learning path
  getSkillBasedTests(skill, userLevel = 'Beginner') {
    const skillTests = this.tests.filter(test =>
      test.skills.some(s => s.toLowerCase().includes(skill.toLowerCase()))
    );

    const levelOrder = { 'Beginner': 1, 'Intermediate': 2, 'Advanced': 3 };
    const userLevelNum = levelOrder[userLevel] || 1;

    return {
      current: skillTests.filter(test => levelOrder[test.difficulty] === userLevelNum),
      next: skillTests.filter(test => levelOrder[test.difficulty] === userLevelNum + 1),
      previous: skillTests.filter(test => levelOrder[test.difficulty] === userLevelNum - 1),
      all: skillTests.sort((a, b) => levelOrder[a.difficulty] - levelOrder[b.difficulty])
    };
  }

