  // Smart test scheduling based on spaced repetition
  getOptimalTestSchedule(userId, timeframeWeeks = 4) {
    const userProfile = this.getUserProfile(userId);
    const learningPath = this.getActiveLearningPath(userId);
    const schedule = [];
    
    if (learningPath) {
      const currentPhase = learningPath.phases.find(phase => !this.isPhaseCompleted(userId, phase));
      if (currentPhase) {
        const testsPerWeek = Math.ceil(currentPhase.tests.length / (timeframeWeeks || 4));
        
        currentPhase.tests.forEach((testId, index) => {
          const weekNumber = Math.floor(index / testsPerWeek) + 1;
          const test = this.getTestById(testId);
          
          schedule.push({
            week: weekNumber,
            testId,
            title: test?.title,
            estimatedDuration: test?.duration,
            difficulty: test?.difficulty,
            skills: test?.skills,
            priority: this.calculateTestPriority(userId, testId),
            optimalDay: this.calculateOptimalStudyDay(userId, index)
          });
        });
      }
    }
    
    return schedule.sort((a, b) => a.week - b.week || b.priority - a.priority);
  }

