  identifySkillGaps(userId) {
    const userProfile = this.getUserProfile(userId);
    const marketDemand = this.getMarketDemandData();
    const skillGaps = [];
    
    Object.entries(marketDemand).forEach(([skill, demand]) => {
      const userLevel = userProfile.skillLevels.get(skill) || 0;
      const requiredLevel = demand.averageRequirement || 3;
      
      if (userLevel < requiredLevel) {
        const gap = requiredLevel - userLevel;
        skillGaps.push({
          skill,
          currentLevel: userLevel,
          requiredLevel,
          gap,
          marketDemand: demand.demandScore,
          salaryImpact: demand.salaryImpact,
          recommendedTests: this.getTestsForSkill(skill)
        });
      }
    });
    
    return skillGaps.sort((a, b) => (b.gap * b.marketDemand) - (a.gap * a.marketDemand));
  }

