  generateUserSummary(sessions) {
    const totalTests = sessions.length;
    const averageScore = sessions.reduce((sum, s) => sum + s.score, 0) / totalTests || 0;
    const passedTests = sessions.filter(s => s.passed).length;
    const totalTime = sessions.reduce((sum, s) => sum + s.testDuration, 0);

    return {
      totalTestsTaken: totalTests,
      averageScore: Math.round(averageScore),
      passRate: Math.round((passedTests / totalTests) * 100) || 0,
      totalTimeSpent: Math.round(totalTime / (1000 * 60)), // minutes
      testsThisMonth: sessions.filter(s => 
        new Date(s.completedAt) > new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)
      ).length,
      averageIntegrity: Math.round(
        sessions.reduce((sum, s) => sum + (s.integrityReport?.finalIntegrityScore || 0), 0) / totalTests
      ) || 0
    };
  }

  analyzeUserPerformance(sessions) {
    const scoresByCategory = {};
    const timesByCategory = {};
    const trendData = [];

    sessions.forEach(session => {
      const test = this.getTestById(session.testId);
      const category = test?.category || 'Unknown';
      
      if (!scoresByCategory[category]) {
        scoresByCategory[category] = [];
        timesByCategory[category] = [];
      }
      
      scoresByCategory[category].push(session.score);
      timesByCategory[category].push(session.testDuration);
      
      trendData.push({
        date: session.completedAt,
        score: session.score,
        category: category
      });
    });

    const performance = {
      categoryPerformance: {},
      overallTrend: this.calculateTrend(trendData),
      strongestCategory: null,
      weakestCategory: null,
      improvementAreas: []
    };

    // Calculate category averages
    Object.keys(scoresByCategory).forEach(category => {
      const scores = scoresByCategory[category];
      const times = timesByCategory[category];
      
      performance.categoryPerformance[category] = {
        averageScore: Math.round(scores.reduce((a, b) => a + b, 0) / scores.length),
        testCount: scores.length,
        averageTime: Math.round(times.reduce((a, b) => a + b, 0) / times.length / 60000), // minutes
        improvement: this.calculateCategoryTrend(trendData.filter(d => d.category === category))
      };
    });

    // Find strongest and weakest categories
    const categoryScores = Object.entries(performance.categoryPerformance);
    if (categoryScores.length > 0) {
      performance.strongestCategory = categoryScores.reduce((a, b) => 
        a[1].averageScore > b[1].averageScore ? a : b
      )[0];
      
      performance.weakestCategory = categoryScores.reduce((a, b) => 
        a[1].averageScore < b[1].averageScore ? a : b
      )[0];
    }

    return performance;
  }

  analyzeUserIntegrity(sessions) {
    const integrityScores = sessions.map(s => s.integrityReport?.finalIntegrityScore || 100);
    const trustLevels = sessions.map(s => s.integrityReport?.trustLevel || 'HIGH');
    
    const flaggedSessions = sessions.filter(s => 
      s.integrityReport && s.integrityReport.finalIntegrityScore < 70
    );

    return {
      averageIntegrityScore: Math.round(integrityScores.reduce((a, b) => a + b, 0) / integrityScores.length),
      trustLevelDistribution: this.calculateDistribution(trustLevels),
      flaggedSessionsCount: flaggedSessions.length,
      commonViolations: this.analyzeCommonViolations(flaggedSessions),
      integrityTrend: this.calculateIntegrityTrend(sessions),
      riskLevel: this.calculateRiskLevel(flaggedSessions.length, sessions.length)
    };
  }

  analyzeUserSkills(sessions) {
    const skillScores = {};
    const skillCounts = {};

    sessions.forEach(session => {
      const test = this.getTestById(session.testId);
      if (test && test.skills) {
        test.skills.forEach(skill => {
          if (!skillScores[skill]) {
            skillScores[skill] = [];
            skillCounts[skill] = 0;
          }
          skillScores[skill].push(session.score);
          skillCounts[skill]++;
        });
      }
    });

    const skillAnalysis = {};
    Object.keys(skillScores).forEach(skill => {
      const scores = skillScores[skill];
      skillAnalysis[skill] = {
        averageScore: Math.round(scores.reduce((a, b) => a + b, 0) / scores.length),
        testCount: skillCounts[skill],
        proficiencyLevel: this.calculateProficiencyLevel(scores),
        trend: this.calculateSkillTrend(skill, sessions)
      };
    });

    return {
      skills: skillAnalysis,
      topSkills: Object.entries(skillAnalysis)
        .sort((a, b) => b[1].averageScore - a[1].averageScore)
        .slice(0, 5)
        .map(([skill, data]) => ({ skill, ...data })),
      skillsToImprove: Object.entries(skillAnalysis)
        .filter(([_, data]) => data.averageScore < 70)
        .sort((a, b) => a[1].averageScore - b[1].averageScore)
        .slice(0, 3)
        .map(([skill, data]) => ({ skill, ...data }))
    };
  }

